mapper.select:
select t.*,mr.meetRoomName,u.phone from tradeorder_detail t LEFT JOIN meetRoom mr ON mr.id=t.meetRoomID LEFT JOIN user_info u on u.userid=t.creator where 1=1
and t.id = #{id,jdbcType=BIGINT}
and t.glide_no = #{glideNo,jdbcType=VARCHAR}
and str_to_date(t.meetDate,'%Y-%m-%d') = str_to_date(#{meetDate},'%Y-%m-%d')
and str_to_date(t.meetStartTime, '%H:%i') >= str_to_date(#{meetStartTime}, '%H:%i')
and str_to_date(t.meetEndTime, '%H:%i') <= str_to_date(#{meetEndTime}, '%H:%i')
and t.err_code = #{errCode}
and t.meetName like '%#{meetName}%'
and t.meetRoomID = #{meetRoomID}


  	 select 
    t.*,mr.meetRoomName,u.phone
    from tradeorder_detail t 
    LEFT JOIN meetRoom mr ON mr.id=t.meetRoomID
LEFT JOIN user_info u on u.userid=t.creator
    where 1=1

		and t.id = #{id,jdbcType=BIGINT}


    	and t.glide_no = #{glideNo,jdbcType=VARCHAR}


    	and str_to_date(t.meetDate,'%Y-%m-%d') =  str_to_date(#{meetDate},'%Y-%m-%d')



		and ((str_to_date(#{meetStartTime}, '%H:%i') <= str_to_date(t.meetEndTime, '%H:%i')
		and str_to_date(#{meetEndTime}, '%H:%i') >= str_to_date(t.meetEndTime, '%H:%i')
		) or (str_to_date(#{meetEndTime}, '%H:%i') >= str_to_date(t.meetStartTime, '%H:%i')
		and str_to_date(#{meetStartTime}, '%H:%i') <= str_to_date(t.meetStartTime, '%H:%i')
		))


    	and t.err_code = #{errCode} 


    	and t.meetName like  '%#{meetName}%' 



    	and t.meetRoomID = #{meetRoomID} 


   	order by t.create_time desc

select td.*,mr.meetRoomName,u.phone from tradeorder_detail td LEFT JOIN meetRoom mr ON mr.id=td.meetRoomID LEFT JOIN user_info u on u.userid=td.creator
and DATE_FORMAT(td.meetDate,'%Y-%m-%d') = DATE_FORMAT(#{meetDate},'%Y-%m-%d')
and td.err_code = #{errCode}
and mr.creator = #{creator}
and td.meetName like CONCAT('%', #{meetName}, '%')
and td.meetRoomID = #{meetRoomID}
order by td.create_time desc
